update.foreach:
UPDATE RAW_SPEC_TB
SET
	ITEM = #{item.item},
	MEMO = #{item.memo},
	STATE = #{item.state}
WHERE SPEC_SEQ = #{item.specSeq}

UPDATE RAW_SPEC_TB
SET
	STATE = #{item.state}
WHERE SPEC_SEQ = #{item.specSeq}

UPDATE RAW_TCHN_TB
SET
	STATE = #{item.state}
WHERE TCHN_SEQ = #{item.tchnSeq}

UPDATE RAW_ASSTN_TB
SET
	STATE = #{item.state}
WHERE ASSTN_SEQ = #{item.asstnSeq}

UPDATE RAW_SYS_TB
SET
	STATE = #{item.state}
WHERE SYS_SEQ = #{item.sysSeq}

UPDATE RAW_CABLE_TB
SET
	STATE = #{item.state}
WHERE CABLE_SEQ = #{item.cableSeq}
